#define ll long long
class Solution {
public:
    ll dp[2001][51], n;
    int mod = 1e9+7;

    ll rec(int i, int prev1, int prev2, vector<int>& nums) {
        // Base case
        if (i == n) return 1;

        if (dp[i][prev1] != -1) return dp[i][prev1];

        ll ans = 0;
        for (int j = prev1; j <= nums[i]; j++) {
            int arr2_i = nums[i] - j;
            if (arr2_i <= prev2) {
                ans += rec(i + 1, j, arr2_i, nums);
                ans %= mod;
            }
        }

        return dp[i][prev1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp, -1, sizeof(dp));
        return rec(0, 0, nums[0], nums);
    }
};
