class Solution {
    int [][] dp;
    int mod = (int )1e9+7;
    public int countOfPairs(int[] a) {
        int n= a.length;
        dp = new int[51][n+1];

        for(int i = 0;i<51;i++)Arrays.fill(dp[i],-1);
        return solve(0,0,a);
    }

    int solve(int i , int j,int [] a){
        if(i==a.length)return 1;
        if(dp[j][i]!=-1)return dp[j][i];
        long ans=0;

        for(int k = j;k<=a[i];k++){
            if(i==0){
                ans+=solve(i+1,k,a);
            }else if((a[i-1]-j)>=(a[i]-k)){
                ans+=solve(i+1,k,a);
            }
            ans%=mod;
        }
        return dp[j][i]=(int )ans;
    }
}
