class Solution {
public:
    int mod = 1e9 + 7;
    long long f(int  i, int prev, vector<int> &nums, vector<vector<long long>> &dp) {
        if(i < 0) return 1;
        if(prev != -1 && dp[i][prev] != -1) return dp[i][prev];
        long long ans = 0;
        for(int k = 0;k<=nums[i]; k++) {
            if(prev == -1) ans += f(i - 1, k, nums, dp);
            else if (k >= prev && (nums[i] - k <= nums[i + 1] - prev)) ans += f(i - 1, k, nums, dp);
            ans %= mod;
        }
        if(prev == -1) return ans;
        return dp[i][prev] = ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>> dp(n + 1, vector<long long> (55, -1));
        return f(n - 1, -1, nums, dp);
    }
};
