class Solution {
public:
    int mod = 1e9 + 7;
    int n;
    vector<vector<vector<int>>> memo;

    int rec(int index, int a1, int a2, vector<int>& nums) {
        if (index == n) {
            return 1;
        }
        if (memo[index][a1][a2] != -1) {
            return memo[index][a1][a2];
        }

        int cnt = 0;
        for (int i = a1; i <= nums[index]; i++) {
            int j = nums[index] - i;
            if (i >= a1 && j <= a2) {
                cnt = (cnt + rec(index + 1, i, j, nums)) % mod;
            }
        }

        return memo[index][a1][a2] = cnt;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();  // Initialize the size of the array
        memo.assign(n, vector<vector<int>>(51, vector<int>(51, -1)));  // Initialize memoization table
        return rec(0, 0, 50, nums);
    }
};
