class Solution {
public:

    int M= 1e9 + 7;

    long long f(int i, int a, int b, vector<int>& nums)
    {
        if(i >= nums.size())
            return 1;
        if(dp[i][a][b] != -1)
            return dp[i][a][b];
        int ans= 0;
        for(int j=a; j<=nums[i]; j++)
        {
            if(j >= a && nums[i]-j <= b)
            {
                ans += f(i+1, j, nums[i]-j, nums);
                ans %= M;
            }
        }
        return dp[i][a][b]= ans%M;
    }

    long long dp[2001][52][52];
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return f(0, 0, 51, nums);
    }
};
