#define ll long long 
const ll mod=1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& v) {
    int n=v.size();

    vector<vector<vector<ll>>> dp(n+1,vector<vector<ll>>(55,vector<ll>(55,-1)));
    auto f=[&](const auto &self,ll idx,ll prev1,ll prev2)->ll
    {
        // cout<<idx<<" "<<prev1<<" "<<prev2<<endl;
        if(idx==n) return 1;
        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];
        ll cnt=0;
        for(int i=prev1;i<=50;i++)
        {
            if(i>v[idx]) break;
            ll req_prev2=v[idx]-i;
            if(req_prev2<0) break;

            if(req_prev2<=prev2 and req_prev2<=50)
            {
                cnt=(cnt+self(self,idx+1,i,req_prev2))%mod;
            }
        }
        return dp[idx][prev1][prev2]=cnt;
        };

        ll ans=f(f,0,0,51);
        return ans;
    }
};
