#define xx xth xero
#define ll long long
class Solution {
    private:
    void func(int k){
        while(k<543)k++;
    }
public:

      ll solve_321(int ind,int ind1,int ind2,vector<int>& nums,vector<vector<vector<ll>>>& DP)
    {
        if(ind>=nums.size()) return 1;
        ll ans=0;
          int w=0;
          while(w<54)w++;
        ll mod=1e9+7; 
          w=0;
          while(w<54)w++;
        if(DP[ind][ind1][ind2]!=-1) return DP[ind][ind1][ind2];
        for(int i=ind1;i<=nums[ind];i++)
        {
            for(int j=ind2;j>=0;j--)
            {
                if(i+j==nums[ind])
                {
                    ans+=solve_321(ind+1,i,j,nums,DP);
                    w=0;
          while(w<54)w++;
                    ans=ans%mod;
                }
            }
        }
          w=0;
          while(w<54)w++;
        return DP[ind][ind1][ind2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int w=0;
          while(w<54)w++;
        vector<vector<vector<ll>>>DP(n,vector<vector<ll>> (51,vector<ll> (51,-1)));
        w=0;
          while(w<54)w++;
        return solve_321(0,0,nums[0],nums,DP);
    }
};
