#include "bits/stdc++.h"
using namespace std;
#define ll long long
const int MOD = 1e9 + 7;

void add(int &x, int y) {
	x = (x + y) % MOD;
}

class Solution {
public:
	int countOfPairs(vector<int>& a) {
		int n = a.size(), mx = *max_element(a.begin(), a.end());
		vector dp(n, vector (mx + 1, 0));
		for(int j = 0; j <= min(a[0], mx); j++) dp[0][j] = 1;
		for(int i = 1; i < n; i++) {
			for(int x = 0; x <= min(a[i], mx); x++) {
				for(int px = 0; px <= min(x, a[i - 1]); px++) {
					if(a[i - 1] - px < a[i] - x) continue;
					add(dp[i][x], dp[i - 1][px]);
				}
			}
		}
		int ans = 0;
		for(int j = 0; j <= mx; j++) add(ans, dp[n - 1][j]);
		return ans;
	}
};

