class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        cache = {}

        def backtrack(i, arr1, arr2):
            if i == n: return 1
            if (i, arr1, arr2) in cache:
                return cache[(i, arr1, arr2)]

            cache[(i, arr1, arr2)] = 0
            for val2 in range(arr2, -1, -1):
                val1 = nums[i] - val2
                if val1 >= arr1 and val2 <= arr2:
                    cache[(i, arr1, arr2)] += backtrack(i + 1, val1, val2)
            return cache[(i, arr1, arr2)]

        return backtrack(0, 0, nums[0]) % MOD
