class Solution {
public:
    int MOD = 1e9+7;
    long long pairs(vector<int>& nums, int n, int inc, int dec, vector<vector<long long>>& dp){
        if(n == nums.size()) return 1;

        if(dp[n][inc+1] != -1) return dp[n][inc+1];

        long long comb = 0;
        for(int i=0; i<=nums[n]; i++){
            if(inc == -1 || (i >= inc && nums[n]-i <= dec))
                comb = (comb + pairs(nums, n+1, i, nums[n]-i, dp))%MOD;
        }

        return dp[n][inc+1] = comb;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<long long>> dp(nums.size()+1, vector<long long>(60, -1));
        return (int)pairs(nums, 0, -1, -1, dp);
    }
};
