class Solution {
    private static final int MOD = 1000000007;

    public int countOfPairs(int[] nums) {
        return dp(nums, 0, 0, new int[nums.length + 1][51]);
    }

    private static int dp(int[] nums, int pos, int a1, int[][] cache) {
        if (pos == nums.length) {
            return 1;
        }
        if (cache[pos][a1] > 0) {
            return cache[pos][a1] - 1;
        }
        int a2 = pos == 0 ? Integer.MAX_VALUE : nums[pos - 1] - a1;
        int result = 0;
        for (int v1 = a1; v1 <= nums[pos]; v1++) {
            int v2 = nums[pos] - v1;
            if (v2 > a2) {
                continue;
            }
            result = (result + dp(nums, pos + 1, v1, cache)) % MOD;
        }
        cache[pos][a1] = result + 1;
        return result;
    }
}
