#define MOD 1000000007
#define ll long long int

class Solution {
public:

    ll fun(int val, int i, vector<int>&a, vector<vector<ll>>&dp) {

        if(i==a.size()) {
            return 1;
        }

        if(dp[val][i]!=-1) {
            return dp[val][i];
        }

        ll ans=0;
        for(int j=val;j<=a[i];j++) {
            ll t=a[i]-j;
            if(i==0 || (a[i-1]-val)>=t) {
                ans=(ans+(fun(j, i+1, a, dp)))%MOD;
            }
        }

        return dp[val][i]=ans;
    }

    int countOfPairs(vector<int>& a) {
        int n=a.size();
        vector<vector<ll>>dp(51, vector<ll>(n, -1));
        return fun(0, 0, a, dp);
    }
};
