class Solution {
public:
    int dp[2001][52][52];
    int mod =  1e9 + 7;
    int solve(vector<int>& nums, int ind, int n1, int n2){
        if(ind >= nums.size())return 1;
        int cnt = 0;
        if(dp[ind][n1 + 1][n2 + 1] != -1)return dp[ind][n1 + 1][n2 + 1];
        int ele = nums[ind];
        if(!ind){
            for(int i = 0; i <= ele; i++){
                cnt = (cnt%mod + solve(nums, ind + 1, i, ele - i)%mod)%mod;
            }
        } else {
            for(int i = n1; i <= ele; i++){
                if( ele - i <= n2){
                    cnt = (cnt%mod + solve(nums, ind + 1, i, ele - i)%mod)%mod;
                }
            }
        }
        return dp[ind][n1 + 1][n2 + 1] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, -1, -1);
    }
};
