#include<iostream>
#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef unsigned long long ull;
typedef double  dbl;
typedef float fl;
#define minPq  priority_queue <vector<ll>, vector<vector<ll>>, greater<vector<ll>>> 
ll mod=1e9+7;
class Solution {
public:
    vector<vector<ll>>dp;
    ll helper(ll prev1,ll prev2,ll ind,vector<int>&nums)
    {
        if(ind==nums.size())
        {
            return 1;
        }
        if(dp[prev1][ind]!=-1)return dp[prev1][ind];
        ll ans=0;
        for(int i=prev1;i<=nums[ind];i++)
        {
            ll crnt1=i;
            ll crnt2=nums[ind]-i;
            if(crnt1>=prev1 && crnt2<=prev2)
            {
                ans+=helper(crnt1,crnt2,ind+1,nums);
                ans%=mod;
            }
        }
        return dp[prev1][ind]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        dp=vector<vector<ll>>(51,vector<ll>(nums.size()+1,-1));
        return helper(0,INT_MAX,0,nums);
    }
};
