class Solution {
public:
int mod=1e9+7;
int ans(int i,int p1,int p2,vector<int>&nums,vector<vector<vector<int>>>&dp){
    if(i>=nums.size())
    return 1;
    if(dp[i][p1][p2]!=-1)
    return dp[i][p1][p2];
    int curr=0;
for(int j=0;j<=nums[i];j++){
    if(j>=p1 && nums[i]-j<=p2)
    curr+=(ans(i+1,j,nums[i]-j,nums,dp))%mod;
    curr=curr%mod;
}
// cout<<i</<" "<<curr<<endl;
return dp[i][p1][p2]=curr%mod;
}
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>>dp(nums.size()+1,vector<vector<int>>(52,vector<int>(52,-1)));        return ans(0,0,50,nums,dp);
    }
};
