#include <bits/stdc++.h>
#ifdef DEBUG
#include "debug.h"
#else
#define dbg(x...)
#endif
using namespace std;

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
		const int mod = 1e9+7;
		const int MAX_K = 1001;

		// dp[i][j]=# of pairs of length i where a[i]<=j
		vector<vector<int>> dp(n, vector<int>(MAX_K+5, 0));
		for (int i = 0; i <= a[0]; ++i) dp[0][i] = 1;
		for (int i = 1; i < n; ++i) {
			for (int j = 1; j <= MAX_K; ++j) {
				dp[i-1][j] += dp[i-1][j-1];
				dp[i-1][j] %= mod;
			}
			for (int j = 0; j <= MAX_K; ++j) {
				// arr2[i] = a[i]-j, so we need a[i]-j<=a[i-1]-k => k<=a[i-1]-a[i]+j
				if (j > a[i]) continue;
				int up = min(j, a[i-1]-a[i]+j);
				if (up >= 0) {
					// for (int k = 0; k <= up; ++k) {
					// 	dp[i][j] += dp[i-1][k];
					// 	dp[i][j] %= mod;
					// }
					dp[i][j] += dp[i-1][up];
					dp[i][j] %= mod;
				}
			}
		}
		int ans = 0;
		for (int i = 0; i < MAX_K; ++i) {
			ans += dp[n-1][i];
			ans %= mod;
		}
		dbg(dp);
		return ans;
    }
};
