class Solution {
public:
    int mod = 1e9+7;
    int n;
    int sol(int i, int prev, vector<int> &nums, vector<vector<int>> &dp){
        if(i==n) return 1;
        if(dp[i][prev] != -1) return dp[i][prev];
        int prev2 = 50, cnt = 0;
        if(i-1 >= 0) prev2 = nums[i-1] - prev;
        for(int j=max(prev,0);j<=nums[i];j++){
            if(prev2 >= (nums[i]-j)) cnt = (cnt + sol(i+1,j,nums,dp)%mod)%mod;
        }
        return dp[i][prev] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51,-1));
        return sol(0,0,nums,dp);
    }
};
