class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        """



        """

        M = 10 ** 9 + 7
        n = len(nums)
        m = max(nums)
        dp = [[0 for _ in range(m+1)] for _ in range(n)]
        dp[0] = [1] * (nums[0]+1)

        for i in range(1, n):
            for curr_val in range(nums[i]+1):
                for prev_val in range(min(nums[i-1], curr_val)+1):
                    if nums[i]-curr_val <= nums[i-1]-prev_val:
                        # if i == n-1 and curr_val == 0: print(prev_val, curr_val, nums[i-1]-prev_val, nums[i]-curr_val)
                        dp[i][curr_val] = (dp[i][curr_val] + dp[i-1][prev_val]) % M

        # print(dp)
        # print(sum(dp[-1]))
        # print(dp)
        return sum(dp[-1]) % M
