int dp[2005][55];
class Solution {
public:

    typedef long long ll;
    const ll MOD = 1e9+7;
    ll solve(vector<int> &nums, int pos, int last){
        if(nums.size() == pos){
            return 1;
        }

        if(last != -1 && dp[pos][last] != -1) return dp[pos][last];

        ll res = 0;
        for(int i = last ; i <= nums[pos]  ; ++i){
            if( pos > 0 && nums[pos - 1] - last < nums[pos] - i){
                continue;
            }
            res += solve(nums, pos + 1, i);
            res %= MOD;
        }
        if( last == -1 ) return res;
        return dp[pos][last] = res;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(nums, 0, 0);
    }
};
