class Solution {
public:
    int mod=1e9+7;
    vector<vector<vector<int>>>dp;
    int solve(vector<int>&nums,int i,int prev1,int prev2){
        if(i>=nums.size()){
            // cout<<prev1<<endl;
            return 1;

        }
        // cout<<prev1<<" "<<prev2<<endl;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int s=0;
        int ans=0;
        for(s=prev1;s<=nums[i];s++){
            int y=nums[i]-s;
            if(y<=prev2){
                ans+=solve(nums,i+1,s,y);
                ans=ans%mod;
            }
        }
        return dp[i][prev1][prev2]=ans%mod;

    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size()+1,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(nums,0,0,50)%mod;
    }
};
