class Solution {
public:
    int dp[2001][51];
    int n;
    int mod = 1e9+7;
    int rec(int level,int last,vector<int> &nums){
        if(level==n){
            return 1;
        }
        if(dp[level][last]!=-1)return dp[level][last];
        int ans = 0;
        for(int i=0;i<=nums[level];i++){
            if(i>=last && (nums[level]-i)<=(nums[level-1]-last)){
                ans = (1LL*ans + 1LL*rec(level+1,i,nums))%mod;
            }
        }
        return dp[level][last]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp,-1,sizeof(dp));
        int ans = 0;
        for(int i=0;i<=nums[0];i++){
            ans = (1LL*ans + 1LL*rec(1,i,nums))%mod;
        }
        return ans;
    }
};
