class Solution {
public:
    int mod = 1000000007;
    vector<vector<vector<int>>> dp;
    int solve(int i, int a1, int a2, vector<int>& nums) {
        if (i == nums.size())
            return 1;
        if (dp[i][a1 + 50][a2 + 50] != -1)
            return dp[i][a1 + 50][a2 + 50];
        int t = nums[i];
        int ans = 0;
        for (int j = 0; j <= t; j++) {
            if (j < a1 || t - j > a2)  
                continue;
            ans = (0LL + ans + solve(i + 1, j, t - j, nums)) % mod;
        }
        return dp[i][a1 + 50][a2 + 50] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(101, vector<int>(101, -1)));
        return solve(0, -50, 50, nums);
    }
};
