class Solution {
public:
    int mod=1e9+7;
    int dp[2000][52];
    int solve(int i,int prev,int n,vector<int> &nums){
        if(i==n) return 1;
        if(dp[i][prev+1]!=-1) return dp[i][prev+1];
        long long ans=0;
        if(prev==-1){
            for(int j=0;j<=nums[i];j++){
                ans+=solve(i+1,j,n,nums);
                ans%=mod;
            }
        }
        else{
            for(int j=prev;j<=nums[i];j++){

                int b=nums[i]-j;
                if(b<=nums[i-1]-prev) ans+=solve(i+1,j,n,nums);
                ans%=mod;
            }
        }
        return dp[i][prev+1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums){

        int n=nums.size();
        memset(dp,-1,sizeof dp);
        return solve(0,-1,n,nums);
    }
};
