class Solution:
    def countOfPairs(self, ar: List[int]) -> int:
        MOD = 10**9+7
        dp = {}
        def sol(i, a, b):
            if i == len(ar): return 1
            if (i, a, b) in dp: return dp[i, a, b]
            res = 0
            for k in range(0, ar[i]+1):
                if a > k or ar[i]-k > b: continue
                res += sol(i+1, k, ar[i]-k)
            dp[i, a, b] = res % MOD
            return res%MOD
        return sol(0, 0, MOD)
