class Solution {
public:
    int mod = 1000000007;
    int sol(int index,int firstMin,int secondMax,vector<int>& nums,vector<vector<vector<int>>>& dp){
        if(index==nums.size()){
            return 1;
        }
         // Check if the result is already computed
    if (dp[index][firstMin][secondMax] != -1) {
        return dp[index][firstMin][secondMax];
    }

        int ans=0;
        for(int i=firstMin;i<=nums[index];i++){
            if(nums[index]-i<=secondMax)
            ans=(ans+ sol(index+1,i,nums[index]-i,nums,dp))%mod;
        }
        return dp[index][firstMin][secondMax]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
    // Initialize a 3D DP table with -1
    vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(nums[0] + 1, -1)));

        return sol(0,0,nums[0],nums,dp);
