class Solution {

    private final int MOD = (int)1e9 + 7;

    private long findCountOfMonotonicArrayPairs(int[] nums, int index, int previousIncreasing, int previousDecreasing, Long[][][] memo){
        if(index == nums.length) return 1;

        if(memo[index][previousIncreasing][previousDecreasing] != null){
            return memo[index][previousIncreasing][previousDecreasing];
        }

        long answer = 0;
        for(int i = previousIncreasing; i <= nums[index]; i += 1){
            int decreasing = nums[index] - i;
            if(decreasing <= previousDecreasing){
                answer += findCountOfMonotonicArrayPairs(nums, index + 1, i, decreasing, memo);
                answer %= MOD;
            }
        }

        return memo[index][previousIncreasing][previousDecreasing] = answer;
    }

    public int countOfPairs(int[] nums) {
        long answer = findCountOfMonotonicArrayPairs(nums, 0, 0, 50, new Long[nums.length][51][51]);
        return (int)answer;
    }
}
