class Solution {
public:
const int mod=1e9+7;
#define ll long long

    int  solve(int ind,vector<vector<vector<int>>>&dp,vector<int>&num,int prev,int prev1){
        if(ind<0){
            return 1;
        }
        else if(dp[ind][prev][prev1]!=-1){
            return dp[ind][prev][prev1];
        }
        int cnt=0;
        for(int i=min(prev,num[ind]);i>=0;i--){
            if(i<=prev and num[ind]-i>=prev1){
                cnt+=solve(ind-1,dp,num,i,num[ind]-i);
            }
            cnt%=mod;
        }
        return dp[ind][prev][prev1]=cnt;

    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();

        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(52,vector<int>(52,-1)));
           return  solve(n-1,dp,nums,51,0);
    }
};
