class Solution {
   long long  LIS(int i, vector<int>& nums,long long  a,long long  b, vector<vector<int>>& dp) {
    long long MOD=1e9+7; 
        if (i == nums.size()) return 1;
        if (dp[i][a] != -1) return dp[i][a];

       long long  ans = 0;
        for (int j = 0; j <= nums[i]; j++) {
            if (j >= a && nums[i] - j <= b) {
                ans =(ans+LIS(i + 1, nums, j, nums[i] - j, dp))%MOD;
            }
        }

        return dp[i][a] = ans;
    }

public:
   int  countOfPairs(vector<int>& nums) {
       long long  n = *max_element(nums.begin(), nums.end());
        vector<vector<int>> dp(nums.size(), vector<int>(n + 1, -1));
        return (int)LIS(0, nums, 0, 1e8, dp);
    }
};
