class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # dp1[i][j], dp2[i][j] ? 
        # a1[i] + a2[i] = nums[i]
        # a1[1] <= a1[2] <= ... <= a1[n]
        # a2[1] >= a2[2] >= ... >= a2[n]

        mod = 10**9 + 7
        n = len(nums)

        @cache
        def go(pos, last1, last2):
            if pos == n:
                return 1
            ans = 0
            for l1 in range(last1, nums[pos] + 1):
                l2 = nums[pos] - l1
                if l2 <= last2 and l2 >= 0: 
                    ans += go(pos + 1, l1, l2)
                    ans %= mod
                    # print(pos, l1, l2)
            return ans

        return go(0, 0, 50)
