class Solution {
public:

    int mod = 1e9+7;

    int f(vector<int>& nums, int i, int prev1, int prev2, vector<vector<vector<int>>>& dp){
        if (i==nums.size()) return 1;
        if (dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        int ans = 0;
        for (int j=prev1;j<=nums[i];j++){
            int val1 = j;
            int val2 = nums[i] - j;
            if (val2<=prev2){
                ans = ( ans + f(nums, i+1, val1, val2, dp) )%mod;
            }
            // else{
            //     break;
            // }
        }
        return dp[i][prev1][prev2] = ans;
    }


    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxi = 0;
        for (auto ele:nums){
            maxi = max(maxi, ele);
        }
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(maxi+1, vector<int>(maxi+1, -1)));

        return f(nums, 0, 0, maxi, dp);
    }
};
