class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp( 51, vector<int>( nums.size(), -1 ) );

        int ans = 0;
        int num = nums[ 0 ];
        for( int i = 0; i <= num; ++i ) {
            ans += dfs( 1, i, num - i, nums, dp );
            ans %= mod;
        }

        return ans;
    }
private:
    const int mod = 1e9 + 7;
    int dfs(int idx, int prev1, int prev2, vector<int>& nums, vector<vector<int>>& dp) {
        if( idx == nums.size() ) {
            return 1;
        }

        if( dp[ prev1 ][ idx ] != -1 ) {
            return dp[ prev1 ][ idx ];
        }

        int num = nums[ idx ];
        int ans = 0;

        for( int i = prev1; i <= num; ++i ) {
            if( num - i > prev2 ) {
                continue;
            }
            ans += dfs( idx + 1, i, num - i, nums, dp );
            ans %= mod;
        }

        return dp[ prev1 ][ idx ] = ans;
    }
};
