class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        N = len(nums)
        MOD = 10 ** 9 + 7

        @cache
        def go(index, last_up):
            if index == N:
                return 1
            last_down = nums[index - 1] - last_up

            total = 0
            for i in range(nums[index] + 1):
                if i >= last_up and nums[index] - i <= last_down:
                    total += go(index + 1, i)
            total %= MOD
            return total

        total = 0
        for i in range(nums[0] + 1):
            total += go(1, i)
        total %= MOD
        return total
