class Solution {
public:
const int MOD = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
         int n = nums.size();
        std::vector<std::vector<int>> dp(n + 1, std::vector<int>(51, 0));


        for (int j = 0; j <= 50; ++j) {
            if (j <= nums[0]) {
                dp[0][j] = 1;  
            }
        }


        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= 50; ++j) {
                if (j <= nums[i]) {
                    int arr2_i = nums[i] - j;
                    for (int k = 0; k <= j; ++k) {
                        if (arr2_i <= nums[i - 1] - k) {
                            dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                        }
                    }
                }
            }
        }


        int result = 0;
        for (int j = 0; j <= 50; ++j) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
};
