class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        @cache
        def dfs(i, a, b):
            if i == len(nums):
                return 1
            res = 0
            for num in range(a, 51):
                if nums[i] - num <= b and nums[i]-num >= 0:
                    res += dfs(i+1, num, nums[i]-num)
            res %= 10**9+7
            return res
        return dfs(0, 0, 50)
