class Solution {
public:
            int mod=1e9+7;
    int countOfPairs(vector<int>& nums) {
        unordered_map<int,long long>dp,ndp;
        for(int i=0;i<=nums[0];i++)dp[i]++;
        for(int i=1;i<nums.size();i++){
            for(auto[x,y]:dp){
                for(int j=nums[i];j>=x;j--){
                    if(nums[i]-j<=nums[i-1]-x){
                        ndp[j]=(ndp[j]+y)%mod;
                    }else break;
                }
            }
            dp.clear();
            swap(dp,ndp);
        }
        long long res=0;
        for(auto[x,y]:dp)res=(res+y)%mod;
        return res;
    }
};
