class Solution {
public:
    int MOD = 1e9 + 7;
    vector<vector<int>> dp;
    int solve(int i, int k, vector<int> &nums, int n){
        if(i == n){
            return 1;
        }
        if(dp[i][k+1] != -1){
            return dp[i][k+1];
        }
        int ans = 0;
        for(int num = 0; num <= nums[i]; num++){
            int cnum = nums[i] - num;
            if(i==0 || (num<=k && cnum >= nums[i-1] - k)){
                ans = (ans + solve(i+1, num, nums, n))%MOD;
            }
        }
        return dp[i][k+1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<int>>(n+1, vector<int>(52, -1));
        return solve(0, -1, nums, n);
    }
};
