class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dic = defaultdict(int)
        prev = None
        mod = 10 ** 9 + 7
        n = len(nums)
        def func(i, prev):
            if i >= n:
                return 1
            if (i, prev) in dic:
                return dic[(i, prev)]
            ret = 0
            comp = nums[i - 1] - prev if i - 1 >= 0 else float('inf')
            for j in range(prev, nums[i] + 1):
                if nums[i] - j <= comp:
                    ret = (ret + func(i + 1, j)) % mod
            dic[(i, prev)] = ret
            return ret
        return func(0, 0)
