function countOfPairs(nums: number[]): number {
    const MOD = 1e9 + 7;
    const cache = new Array(nums.length).fill(undefined).map(() => new Array(50).fill(undefined));

    function solve(idx: number, prevVal: number) {
        if(idx === nums.length) return 1;
        if(cache[idx][prevVal] !== undefined) return cache[idx][prevVal];

        let count = 0;

        for(let val = prevVal; val <= nums[idx]; val++) {
            const val2 = nums[idx] - val;
            if(idx === 0 || val2 <= (nums[idx - 1] - prevVal)) 
                count = (count + solve(idx + 1, val)) % MOD;
        }

        return cache[idx][prevVal] = count;
    }

    return solve(0, 0);
};
