class Solution {
public:
    int mod = 1000000007;

    int helper(vector<int>& nums, int index, int n, int prevLow, int prevHigh, vector<vector<int>>& dp) {
        if (index == n) {
            return 1;
        }
        if (dp[index][prevLow] != -1) return dp[index][prevLow];
        int sum = 0;
        for (int i = prevLow; i <= nums[index]; i++) {
            if (nums[index] - i <= prevHigh) {
                sum += helper(nums, index + 1, n, i, nums[index] - i, dp);
                sum %= mod;
            }
        }
        return dp[index][prevLow] = sum;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(n + 60, -1));
        return helper(nums, 0, n, 0, nums[0], dp);
    }
};
