class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod=10**9 + 7
        n=len(nums)
        ans=0
        @cache
        def foo(i,fw,bc):
            if i==n:
                return 1
            temp=0
            for x in range(nums[i]+1):
                y=nums[i]-x
                if x>=fw and y<=bc:
                    temp+=foo(i+1,x,y)
            return temp
        for x in range(nums[0]+1):        
            ans+=foo(1,x,nums[0]-x)
            ans%=mod
        return ans
