class Solution {
    using ll = long long;
    ll mod = 1e9 + 7;
    int lim = 1001;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector <vector <ll>> dp(n, vector <ll> (lim, 0));
        for (int i = 0; i < n; i ++) {
            if (i == 0) {
                for (int j = 0; j <= nums[i]; j ++) {
                    dp[i][j] = (dp[i][j] + 1) % mod;
                }
            }
            else {
                for (int j = 0; j <= nums[i]; j ++) {
                    int k = min({j, nums[i - 1], j + nums[i - 1] - nums[i]});
                    if (k >= 0) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % mod;
                    }
                }
            }
            for (int j = 1; j < lim; j ++) {
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod;
            }
        }
        return dp[n - 1][lim - 1];
    }
};
