MOD = 1_000_000_007
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def solve(curr, a, b):
            if a<0 or b<0:
                return 0
            if curr>=len(nums):
                return 1

            ret = 0
            for i in range(b+1):
                if nums[curr]-i>=a:
                    ret += solve(curr+1,nums[curr]-i,i)
            return ret

        return solve(0,0,nums[0])%MOD
