const int MOD = 1000000007 ; // 998244353 

class Solution {
public:
    int n;
    vector<vector<vector<long long>>>dp;
    vector<int>nums;
    long long rec(int idx,int val1,int val2){
        if(idx>=n) return 1;
        if(dp[idx][val1][val2] != -1) return dp[idx][val1][val2];
        long long ans=0;
        for(int j=0;j<=nums[idx];++j){
            if(j>=val1 and (nums[idx]-j)<=val2){
                ans+=rec(idx+1,j,nums[idx]-j);
                ans%=MOD;
            }
            else if(val1==0 and val2==0){
                ans+=rec(idx+1,j,nums[idx]-j);
                ans%=MOD;
            }
        }
        return dp[idx][val1][val2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        this->nums=nums;
        n=nums.size();
        int mx=*max_element(nums.begin(),nums.end());
        dp=vector<vector<vector<long long>>>(n+1,vector<vector<long long>>(mx+2,vector<long long>(mx+2,-1)));
        return (rec(0,0,0)%MOD);
    }
};
