class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> prev, curr;
        for (int num1 = 0; num1 <= nums[0]; num1++) {
            int num2 = nums[0] - num1;
            prev.push_back({num1, num2, 1});
        }

        for (int i = 1; i < n; i++) {
            curr.clear();
            for (int num1 = 0; num1 <= nums[i]; num1++) {
                int num2 = nums[i] - num1;
                int count = 0;
                for (auto& p : prev) {
                    if (p[0] > num1) continue;
                    if (p[1] < num2) continue;
                    count = (count + p[2]) % MOD;
                }
                if (count > 0) curr.push_back({num1, num2, count});
            }
            swap(prev, curr);
        }

        int result = 0;
        for (auto& p : prev) result = (result + p[2]) % MOD;
        return result;
    }
private:
    const int MOD = 1e9 + 7;
};
