class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def countOfPairsHelper(i, a, b):
            if i >= len(nums):
                return 1
            res = 0
            for n in range(a, nums[i] + 1):
                if nums[i] - n > b:
                    continue
                res += countOfPairsHelper(i + 1, n, nums[i] - n)
            return res

        MOD = int(1E9) + 7
        return countOfPairsHelper(0, 0, 100) % MOD
