MOD = 1000000007
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0] * 51 for _ in range(n)]
        for j in range(nums[0] + 1):
            dp[0][j] = 1
        for i in range(1, n):
            for cur in range(nums[i] + 1):
                for prv in range(cur + 1):
                    if prv <= cur and nums[i - 1] - prv >= nums[i] - cur:
                        dp[i][cur] += dp[i - 1][prv]
                        dp[i][cur] %= MOD
        return sum(dp[-1]) % MOD
