#define MOD 1000000007



class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int N=nums.size();
        nums.push_back(0);
        vector<vector<int>>dp(N,vector<int>(51,0));
        for(int i=0;i<=nums[0];i++)dp[0][i]=1;
        for(int i=0;i<N-1;i++)for(int j=0;j<=50;j++)if(dp[i][j]){
            int cur1=j,cur2=nums[i]-j;
            for(int k=cur1;k<=nums[i+1];k++)if(nums[i+1]-k<=cur2)dp[i+1][k]=(dp[i+1][k]+dp[i][j])%MOD;
        }
        int sol=0;
        for(int i=0;i<=50;i++)sol=(sol+dp[N-1][i])%MOD;
        return sol;
    }
};
