class Solution {
public:
    int dp[2001][51];
    #define mod 1000000007
    int rec(int n, int i, int prev, vector<int>&nums){
        if (i==n) {
            return 1;
        }
        if (dp[i][prev]!=-1) return dp[i][prev];
        int cnt=0;
        for (int j=0; j<=nums[i]; j++){
            if (i==0) {
                cnt = (cnt + rec(n, i+1, j, nums))%mod;
            } 
            else {
                if (j>=prev and nums[i]-j<=nums[i-1]-prev) {
                    cnt=(cnt + rec(n, i+1, j, nums))%mod;
                }
            }
        }
        return dp[i][prev]=cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp, -1, sizeof(dp));
        return rec(n, 0, 0, nums);
    }
};
