package main

import "fmt"

const modVal = 1000000007

func solve(index, a, b int, nums []int, memo [][][]int) int {
    if index >= len(nums) {
        return 1
    }
    if memo[index][a][b] != -1 {
        return memo[index][a][b]
    }

    result := 0
    for i := a; i <= nums[index]; i++ {
        for j := b; j >= 0; j-- {
            if i + j == nums[index] {
                result = (result + solve(index + 1, i, j, nums, memo)) % modVal
            }
        }
    }
    memo[index][a][b] = result
    return result
}

func countOfPairs(nums []int) int {
    size := len(nums)
    memo := make([][][]int, size)
    for i := range memo {
        memo[i] = make([][]int, 51)
        for j := range memo[i] {
            memo[i][j] = make([]int, 51)
            for k := range memo[i][j] {
                memo[i][j][k] = -1
            }
        }
    }
    return solve(0, 0, nums[0], nums, memo)
}
