class Solution {
    // arr2[i] == nums[i] - arr1[i]
    // arr1[i] <= arr1[i + 1]
    // nums[i] - arr1[i] >= nums[i] - nums[i + 1];
    const int MOD = 1e9 + 7;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int max_val = *max_element(nums.begin(), nums.end());
        vector<vector<long>> f(n, vector<long>(max_val + 1, 0));
        for(int j = 0; j <= nums[0]; ++j){
            f[0][j] = 1;
        }

        for(int i = 1; i < n; ++i){
            for(int j = 0; j <= nums[i]; ++j){
                // k <= j
                // nums[i - 1] - 
                for(int k = 0; k <= j; ++k){
                    if(nums[i - 1] - k >= nums[i] - j){
                        f[i][j] += f[i - 1][k];
                        f[i][j] %= MOD;
                    }
                } 
            }
        }

        long res = 0;
        for(int j = 0; j <= max_val; ++j){
            res += f[n - 1][j];
            res %= MOD;
        }

        return res;
    }
};
